import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';
// import{ Constants } from './config/constants'; 
import { Injectable } from '@angular/core';
import { DataService } from '../dataservice';


@Injectable({
  providedIn: 'root'
})

@Component({
  selector: 'app-frontpage',
  templateUrl: './frontpage.component.html',
  styleUrls: ['./frontpage.component.css']
})
export class FrontpageComponent {
  constructor(private dataService: DataService){ }
data: any;
cityName:any; Lat: any; Lon: any;
dataFlag = false;

  onCall(cityName: string){
    console.log('hereh');
    this.dataService.getData(cityName).subscribe(data => {
      this.dataFlag = true;
      this.data = data;
      console.log(this.data.results[0].name);
      this.cityName;
      this.Lat = this.data.results[0].latitude;
      this.Lon = this.data.results[0].longitude;

    });
  }
}
